import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import MovieList from '../components/MovieList';

const MoviesPage = () => {
  const [movies, setMovies] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:4000/movies');
        setMovies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div>
      <h2>Категорий өту</h2>
      <Link to="/categories" className="btn btn-primary mb-2">Add categories</Link>
      <h2>Категорий құру</h2>
      <Link to="/categories/new" className="btn btn-primary mb-2">New categories</Link>
      <div>
        <label htmlFor="category">Category:</label>
        <select id="category" onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">All categories</option>
          {/* Add options for categories here */}
        </select>
      </div>
      <MovieList movies={selectedCategory ? movies.filter((movie) => movie.categoryId === selectedCategory) : movies} />
    </div>
  );
};

export default MoviesPage;
