import { Link, Outlet, useNavigate, useParams } from "react-router-dom";
import { useEffect,useState } from "react";
import { getCategoryPosts, getAllUsers } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CategoryPosts(){

    const navigate = useNavigate();
    const params = useParams();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const loadPosts = async () => {
            const {success, data } = await getCategoryPosts(params.catId);
            if(success){
                setPosts(data);
            }
            else{
                navigate("/login");
            }
        }
        loadPosts();
    },[params]);

    return(
        <div className="container">
            <div className="row">
                    {
                        posts.map((post) => {
                            return(
                                <div className="col-sm-6" key={post.id}>
                                    <div className="card mt-4 py-3 mx-5" >
                                        <div className="card-body">
                                            <h5 className="card-title"><Link className="nav-link" to={'/posts/'+post.id} >{post.title}</Link></h5>
                                            <p className="card-text"> Price : { post.price}</p>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            <div>
                <Outlet/>
            </div>
        </div>
    )
};
