import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HomePage = () => {
    const [user, setUser] = useState(null);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const loggedInUser = localStorage.getItem('token'); // Token stored in localStorage
        if (loggedInUser) {
            // Assume a simplified decode; implement as necessary
            const decodedUser = JSON.parse(atob(loggedInUser.split('.')[1]));
            setUser(decodedUser.username); // Assuming the token stores username
        }
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        const response = await axios.get('http://localhost:3005/categories');
        setCategories(response.data);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setUser(null);
    };

    const handleSearch = async (event) => {
        event.preventDefault();
        const searchValue = event.target.search.value.toLowerCase();
        const category = categories.find(c => c.name.toLowerCase() === searchValue);
        if (category) {
            setSelectedCategory(category);
            const moviesResponse = await axios.get(`http://localhost:3005/movies/category/${category.id}`);
            setMovies(moviesResponse.data);
        }
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg bg-body-tertiary">
                <div className="container-fluid">
                    <Link className="navbar-brand" to="/">FilmApp</Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            {user ? (
                                <>
                                    <li className="nav-item">
                                        <a className="nav-link" href="#" onClick={handleLogout}>Logout ({user})</a>
                                    </li>
                                </>
                            ) : (
                                <>
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/register">Register</Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link className="nav-link" to="/login">Login</Link>
                                    </li>
                                </>
                            )}
                        </ul>
                        <form className="d-flex" role="search" onSubmit={handleSearch}>
                            <input className="form-control me-2" type="search" placeholder="Search categories" aria-label="Search" name="search"/>
                            <button className="btn btn-outline-success" type="submit">Search</button>
                        </form>
                    </div>
                </div>
            </nav>
            <div className="container mt-4">
                {selectedCategory && movies.length > 0 && (
                    <>
                        <h2>{selectedCategory.name} Movies</h2>
                        <div className="row">
                            {movies.map(movie => (
                                <div className="col-md-4" key={movie.id}>
                                    <div className="card" style={{ width: '18rem' }}>
                                        <img src={movie.poster_url} className="card-img-top" alt={movie.title}/>
                                        <div className="card-body">
                                            <h5 className="card-title">{movie.title}</h5>
                                            <p className="card-text">{movie.description}</p>
                                            <Link to={`/movies/${movie.id}`} className="btn btn-primary">View Details</Link>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default HomePage;
