import React from 'react';

const ImageDisplay = ({ imageData }) => {
  if (!imageData) {
    return <div>Пожалуйста, введите шестнадцатеричное значение цвета.</div>;
  }

  return <img src={`data:image/png;base64,${imageData}`} alt="Изображение" />;
};

export default ImageDisplay;
