import { useState,useEffect  } from "react";
import { getOneProd,Update,getCategory } from "./api";
import 'react-notifications/lib/notifications.css';
import {useParams} from "react-router-dom";

export default function Edit(){
    const params = useParams();
    const [category, setCategory] = useState([]);
    
    useEffect(() => {
        const getCategories = async () => {
            const response = await getCategory();
            setCategory(response);
        }
        getCategories();
    }, []);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const product = await getOneProd(params.prodId);
                if (product) {
                    setProd(product);
                }
            } catch (error) {
                console.error("Error product:", error);
            }
        };

        if (params.prodId) {
            fetchData();
        }
    }, [params.prodId]);
    

    const[prod,setProd]=useState({
        name: '',
        price:0,
        category:''
    });
    const handleName =(ev)=>{
        setProd({...prod,name: ev.target.value});
    }
    const handlePrice =(ev)=>{
        setProd({...prod,price: ev.target.value});
    }
    const Select =(ev)=>{
        setProd({...prod,category:ev.target.value});
        console.log(ev.target);
    }  

    const handleForm= async(ev)=>{
        ev.preventDefault();
        const update = await Update(prod);
        console.log("update:"+ update);
    }

    return (
        <div>
            <form onSubmit={handleForm} className="container mt-5">
                {prod.image && <img src={require(`./images/${prod.image}`)} alt="a" />}
                <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input id="name" type="text" className="form-control" value={prod.name} onChange={handleName} />
                </div>
                <div className="form-group">
                    <label htmlFor="price">Price:</label>
                    <input id="price" type="number" className="form-control" value={prod.price} onChange={handlePrice} />
                </div>
                <div className="form-group">
                    <label htmlFor="category">Category:</label>
                    <select id="category" className="form-control" value={prod.category} onChange={Select}>
                        {category.map((op) => {
                            return (
                                <option key={op.id} value={op.id}>{op.name}</option>
                            )
                        })}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">
                    Update
                </button>
            </form>
        </div>
    )
}
